
public class ternaryOpretoe {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i=1;
		int j=3;
		
		//we can wright like in ternary operator
		
		j = i==1?6:3;// if i is 1 than j is 6 otherwise 3.
		
		if (i==1)
		{
			j=6;
		}
		else {
			j=3;
		}
		System.out.println("output is:"+j);

	}

}
